mapper.select:
select *
from
(
    select rownum as RNUM,A.*
    from
    (
    	select * from users 
    	
    	order by user_no desc
    )A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
    select rownum as RNUM,A.*
    from
    (
    	select * from admin 
    	
    	order by admin_no desc
    )A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update admin
set admin_pwd=#{adminPwd}
where admin_id=#{adminId}
