mapper.select:
select *
from
(
        select rownum as RNUM , A.*, (notice_regdate+30) as endday 
        from  
    (
        select * from notice
		where notice_category=#{noticeCategory}

			and NOTICE_TITLE like  '%'||#{noticeTitle}||'%'

		order by notice_no desc
    )A   
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
        select rownum as RNUM , A.* 
        from  
    (
        select * from notice

			where NOTICE_TITLE like  '%'||#{noticeTitle}||'%'

		order by notice_no desc
    )A   
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

SELECT A.*, (notice_regdate+30) as endday FROM ( SELECT NOTICE_NO, NOTICE_CATEGORY, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_FILENAME, NOTICE_REGDATE, LEAD(NOTICE_TITLE, 1, '다음글이 없습니다') OVER (ORDER BY NOTICE_NO) AS NEXT_TITLE, LEAD(NOTICE_NO, 1) OVER (ORDER BY NOTICE_NO) AS NEXT_NO, LEAD(NOTICE_CATEGORY, 1) OVER (ORDER BY NOTICE_NO) AS NEXT_CATEGORY, LAG(NOTICE_TITLE, 1, '이전글이 없습니다') OVER (ORDER BY NOTICE_NO) AS PRE_TITLE, LAG(NOTICE_NO, 1) OVER (ORDER BY NOTICE_NO) AS PRE_NO, LAG(NOTICE_CATEGORY, 1) OVER (ORDER BY NOTICE_NO) AS PRE_CATEGORY FROM NOTICE
where notice_category=#{noticeCategory}
) A where notice_no=#{noticeNo}

mapper.update:
update notice
set NOTICE_TITLE=#{noticeTitle}, ADMIN_NO=#{adminNo}, NOTICE_CONTENT=#{noticeContent}, NOTICE_CATEGORY=#{noticeCategory},
	NOTICE_FILENAME=#{noticeFilename}, NOTICE_OLDFILENAME=#{noticeOldfilename}
where notice_no=#{noticeNo}
