mapper.update:
update notice
set NOTICE_TITLE=#{noticeTitle}, ADMIN_NO=#{adminNo}, NOTICE_CONTENT=#{noticeContent}, NOTICE_CATEGORY=#{noticeCategory}
where notice_no=#{noticeNo}
